sources.styles.scss:
@import url("https://fonts.googleapis.com/css2?family=Sora:wght@100;300;500;800&display=swap");

$fontThin: 100;
$fontLight: 300;
$fontRegular: 500;
$fontBold: 800;
$mainFont: "Sora", sans-serif;
$blue: rgb(0, 136, 156);

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background: black;
}

header {
  background: url("./img/records.jpg");
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  height: 100vh;
  width: 100vw;
  overflow: hidden;
  filter: blur(2px);
  -webkit-filter: blur(2px);
}

.overlay {
  overflow: hidden;
  height: 100vh;
  width: 100vw;
  z-index: 2;
  background: rgba(0, 0, 0, 0.5);
}

.main-text {
  height: 100vh;
  width: 100vw;
  position: absolute;
  z-index: 3;
  top: 0;
  left: 0;
  margin: auto;
  text-align: center;
}

.main-text h2 {
  margin: auto;
  text-align: center;
  margin-top: 10%;
  color: white;
  font-family: $mainFont;
  letter-spacing: 1em;
  font-weight: $fontBold;
  font-size: 2.5em;
  border: 3px solid white;
  display: inline-block;
  padding: 0.5em;
  -webkit-animation: slide-in-top 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) both;
  animation: slide-in-top 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) both;
}

.main-text p {
  color: white;
  text-transform: uppercase;
  font-family: $mainFont;
  font-weight: $fontLight;
  font-size: 1.5em;
  letter-spacing: 0.7em;
  margin-top: 1.5em;
  -webkit-animation: slide-in-top 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) both;
  animation: slide-in-top 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) both;
}

.main-text a {
  position: relative;
  top: 30vh;
  text-transform: uppercase;
  font-family: $mainFont;
  padding: 10px;
  text-align: center;
  color: white;
  font-size: 1.2em;
  font-weight: $fontLight;
  text-decoration: none;
  letter-spacing: 0.5em;
  border: 2px solid white;
  -webkit-animation: heartbeat 1.5s ease-in-out infinite both;
  animation: heartbeat 1.5s ease-in-out infinite both;
  transition: all 500ms;
}
.main-text a:hover {
  opacity: 0.7;
}

#main-area {
  height: 100vh;
  width: 100vw;
  background: #fbfbfb;
  font-family: $mainFont;
  text-align: center;
  padding: 20px;
}

#main-area h2 {
  font-weight: $fontLight;
  font-size: 1.5em;
  text-transform: uppercase;
  letter-spacing: 0.5em;
  border: 3px solid $blue;
  display: inline-block;
  padding: 0.5em;
  color: $blue;
}

.new-records-container {
  margin-top: 2em;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  grid-gap: 2em;
}

.new-records-col {
  max-width: 22vw;
  min-height: 70vh;
  background: white;
  border-radius: 2%;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
  position: relative;
}

.new-records-col:hover {
  -webkit-animation: wobble-hor-bottom 0.8s both;
  animation: wobble-hor-bottom 0.8s both;
}

.new-records-col img {
  max-width: 22vw;
  margin-bottom: 1em;
}

.new-records-detail {
  font-weight: $fontLight;
  line-height: 1.7em;
}

.title {
  color: $blue;
}

.price {
  color: red;
}
.sale {
  color: white;
  background: red;
  border-radius: 2%;
  margin-left: 2px;
  padding: 2px;
}

.buy-it-now-btn {
  background: linear-gradient(to right, darkred 50%, $blue 50%);
  background-size: 200% 100%;
  background-position: right bottom;
  border-radius: 2%;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
  color: white;
  padding: 0.75em;
  margin-top: 1em;
  transition: all 500ms;
  position: absolute;
  bottom: 0;
  width: 100%;
}

.buy-it-now-btn:hover {
  cursor: pointer;
  background-position: left bottom;
}

//Media Query
@media only screen and (max-width: 600px) {
  body {
    max-width: 100vw;
  }
  header {
    width: 100vw;
    text-align: center;
  }

  .overlay {
    width: 100vw;
    z-index: 2;
  }

  .main-text {
    height: 100vh;
    width: 100vw;
  }

  .main-text h2 {
    font-size: 1em;
    padding:0.5em;
    text-align: center;
    line-height: 2em;
  }

  .main-text p {
    font-size: 1em;
    line-height: 2;
  }

  .main-text a {
    top: 50vh;
    font-size:1em;
  }

  #main-area {
    width: 100vw;
    height: auto;
  }

  .new-records-container {
    grid-template-columns: 1fr;
    text-align: center;
  }
  .new-records-col {
    margin: 0 auto;
    min-width: 90vw;
    max-width: 100vw;
    min-height: 90vh;
  }

  .new-records-col img {
    min-width: 90vw;
    max-width: 90vw;
  }
}

